sp_widget.client_script:
api.controller=function() {
	/* widget controller */
	var c = this;

// Get sys_id of selected record and emit it to sibling widget: My notes body
	c.selectItem = function(idx){
		c.notePos = idx;
		var id = c.data.notes[idx].sys_id;
		console.log("Note ID: "+id);
		$rootScope.noteID = id;
		$rootScope.$emit('selectNote', id);
	};

// Listen to event "updateNote" from sibling widget: My notes body; update title and body
	$rootScope.$on('updateNote', function(event, data){
		c.data.notes[c.notePos].title = $rootScope.updatedNoteTitle;
		c.data.notes[c.notePos].note = $rootScope.updatedNoteBody;
	});
};

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	data.notes = [];

	var grNotes = new GlideRecord("x_snc_createnotes_note");
	grNotes.addQuery('user', gs.getUser().getID());
	grNotes.orderByDesc('sys_created_on');
	grNotes.query();

	// get all the notes record and push to array
	while(grNotes.next()){
		var noteObj = {};

		$sp.getRecordDisplayValues(noteObj, grNotes, 'number, title, sys_id');

		noteObj.note = grNotes.getValue('note').slice(0, 20);

		data.notes.push(noteObj);
	}
})();
